mapper.select:
select * from orders


        and order_time between #{beginTime} and #{endTime}


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and status = #{status}


order by order_time desc

select o.*, od.id as od_id, od.name as od_name, od.image as od_image, od.order_id as od_order_id, od.dish_id as od_dish_id, od.setmeal_id as od_setmeal_id, od.dish_flavor as od_dish_flavor, od.number as od_number, od.amount as od_amount from orders o left join order_detail od on o.id = od.order_id
and user_id = #{userId}
and status = #{status}
order by o.order_time desc

select sum(amount) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select count(id) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}
